INSERT INTO `attraction_order` VALUES ('1929873460323815426','1927337268973895682','乌江寨','2016年10月，贵州乌江寨国际旅游度假区开工建设，于2022年2月1日正式开启试运营，同年5月1日正式开业。 整个景区规划6大板块，主要为田园风光康养区、高山农业体验区、原住村民生活区、温泉商务会展区、山谷露营探险区、初心涧生态度假区。 主要景点有廻（huí）龙塔、 茶马古道、 江岸鱼市、 陈公祠、 麦田剧场、 [12]议事堂等。','http://localhost:8080/img/1748347980284乌江寨1.jpg,http://localhost:8080/img/1748347986599乌江寨2.jpg',1,'2025-06-12 08:00:00','[{\"name\":\"陆金淼\",\"tel\":\"17585442230\",\"idCard\":\"123456789123456789\"}]',3,'c22b637f4eefe4ae1c7866ce8c478cdb','ljm','2025-06-03 20:11:03','超级管理员','2025-06-03 22:01:21',NULL),('1929878841540349953','1927336345790808065','织金洞','织金洞景区内溶沟、溶槽、陡峭的大河谷、天生桥、溶洞处处皆是，主要景点有望月楼、塔林、南天门、万寿山、望山湖、广寒宫、雪香宫、凌霄宫、天都广场、银雨宫等。织金洞景区由织金洞、绮结河、东风湖三个分别代表不同岩溶发育过程的地貌单元组成，织金洞勘察长度12.1千米，洞腔最宽跨度175米，相对高差150米，景区面积15.7平方千米，是一个集科普、探险、研学、游览和度假于一体的旅游景区。','http://localhost:8080/img/1748347691175织金洞1.jpg,http://localhost:8080/img/1748347695040织金洞2.jpg',1,'2025-06-04 08:00:00','[{\"name\":\"ljm\",\"tel\":\"12345678900\",\"idCard\":\"\"}]',2,'c22b637f4eefe4ae1c7866ce8c478cdb','ljm','2025-06-03 20:32:26','ljm','2025-06-03 22:17:50',NULL),('1930220279234134018','1927005411283550210','黄果树瀑布','亚洲四大瀑布之一，黄果树大瀑布位于 贵州省 安顺市 的 镇宁布依族苗族自治县 境内的白水河上游，安顺市西南45千米处， 镇宁布依族苗族自治县 境内，东北距贵州省会 贵阳市 150千米。','http://localhost:8080/img/1748268573521黄果树瀑布1.jpg,http://localhost:8080/img/1748268576793黄果树瀑布3.jpg,http://localhost:8080/img/1748268580981黄果树瀑布4.jpg',2,'2025-06-04 08:00:00','[{\"name\":\"123\",\"tel\":\"123\",\"idCard\":\"\"},{\"name\":\"1234\",\"tel\":\"1234\",\"idCard\":\"\"}]',0,'ce1f444619d7675515d83caa5dac6c7b','lxf','2025-06-04 19:09:11','lxf','2025-06-04 19:09:11',NULL),('test_order_1','1927337268973895682','贵州经典环游','贵州经典环游介绍','http://localhost:8080/img/test.jpg',1,'2025-06-04 20:05:05','[{\"name\":\"张三\",\"tel\":\"12345678900\",\"idCard\":\"123456789012345678\"}]',1,'test_user_1','admin','2025-06-04 20:05:05','admin','2025-06-04 20:05:05','test_line_1');
